mapper.select:

SELECT m.id
FROM member m
         LEFT JOIN arrow a
                   ON a.sender_id = #{memberId} AND a.target_id = m.id
WHERE a.id IS NULL
  AND m.id != #{memberId}
  AND #{minIncludeAge} <= m.age
  AND m.age <= #{maxIncludeAge}
  AND m.gender = 'MALE'
LIMIT ${size}


SELECT m.id
FROM member m
         LEFT JOIN arrow a
                   ON a.sender_id = #{memberId} AND a.target_id = m.id
WHERE a.id IS NULL
  AND m.id != #{memberId}
  AND #{minIncludeAge} <= m.age
  AND m.age <= #{maxIncludeAge}
  AND m.gender = 'FEMALE'
LIMIT ${size}


SELECT m.id
FROM member m
         LEFT JOIN arrow a
                   ON a.sender_id = #{memberId} AND a.target_id = m.id
WHERE a.id IS NULL
  AND m.id != #{memberId}
  AND #{minIncludeAge} <= m.age
  AND m.age <= #{maxIncludeAge}
LIMIT ${size}
