mapper.select:

SELECT m.id
FROM member m
         LEFT JOIN arrow a
                   ON a.sender_id = #{memberId} AND a.target_id = m.id
WHERE a.id IS NULL
  AND m.id != #{memberId}
  AND #{minIncludeAge} <= m.age
  AND m.age <= #{maxIncludeAge}
  AND m.targetGender = #{targetGender}
  AND m.last_active_latitude BETWEEN (#{lat} - (#{maxIncludeDistanceFromMe} / 111.0)) AND (#{lat} + (#{maxIncludeDistanceFromMe} / 111.0))
  AND m.last_active_longitude BETWEEN (#{lng} -
                                       (#{maxIncludeDistanceFromMe} / (111.0 * COS(RADIANS(#{lat}))))) AND (
    #{lng} + (#{maxIncludeDistanceFromMe} / (111.0 * COS(RADIANS(#{lat})))))
  AND (
          6371 * ACOS(
                  COS(RADIANS(#{lat})) * COS(RADIANS(m.last_active_latitude)) *
                  COS(RADIANS(m.last_active_longitude) - RADIANS(#{lng})) +
                  SIN(RADIANS(#{lat})) * SIN(RADIANS(m.last_active_latitude))
                 )
          ) <= #{maxIncludeDistanceFromMe}
ORDER BY m.last_active_date DESC
LIMIT ${size}


SELECT m.id
FROM member m
         LEFT JOIN arrow a
                   ON a.sender_id = #{memberId} AND a.target_id = m.id
WHERE a.id IS NULL
  AND m.id != #{memberId}
  AND #{minIncludeAge} <= m.age
  AND m.age <= #{maxIncludeAge}
  AND m.targetGender = #{targetGender}
LIMIT ${size}
